#include<stdio.h>
int read_line(char str[] ) {
    int i=0;
    char c=getchar();
    while (c!=EOF && c!='\n')
    {
        str[i]=c;
        c=getchar();
        i++;
    }
    str[i]='\0';
    return i;
}
