long long lower_bound(long long *A,long long L,long long R,long long val){
    long long l=L,r=R;
    while(l<r){
    long long m=(l+r)/2;
        if(val<=A[m]){
            r=m-1;
        }else{
            l=m+1;
        }
    }
    return val<=A[l]?l:l+1;
}
