int bound(long long int a[], int n,long long int x) {
    int l = 1;
    int h = n; 
    while (l < h) {
        int mid = (l + h) / 2;
        if (x <= a[mid]) {
            h = mid;
        } else {
            l = mid + 1;
        }
    }
    return l;
}
