	{
		count = 0;
		scanf("%I64d", &temp);
		after = temp - before + beforeR;
		count = beforeC;
		while (after > 0)
		{
			if (after <= room[count])
			{
				printf("%I64d %I64d\n", count + 1, after);
				beforeR = after;
				beforeC = count;
				break;
			}
			else
			{
				after -= room[count];
				count++;
			}
		}
		before = temp;
	}

	return 0;
}

