long long a[N], s[N];
int n, m;

int b_search(long long query) {
    int l = 1, r  = n, m;
    while(l < r) {
        m = (l + r) / 2;
        if (s[m] == query) {
            l = m;
            break;
        } else if (s[m] < query) {
            l = m + 1;
        } else {
            r = m - 1;
        }
    }

    return s[l] > query ? l - 1 : l;
}

