int main()
{
	char name[101];
	int size;
	int i, j;
	int isClear = 0;
	char before = 'a';
	int point, count;
	int maxCount = 0;
	int res = 0;

	scanf("%d", &size);
	scanf("%s", &name);

	while (isClear == 0)
	{
		count = 1;
		before = 'a';
		maxCount = 0;
		for (i = 0; i < size; i++)
		{
			if (name[i] == 'x')
			{
				if (before == 'x')
				{
					count++;
					if(count>=3)
						point = i;
				}
				before = name[i];
				if (maxCount < count)
				{
					maxCount = count;
				}
			}
			else
			{
				if (maxCount < count)
				{
					maxCount = count;
				}
				count = 1;
				before = name[i];
			}
		}
		if (maxCount >= 3)
		{
			for (i = point; i < size-1; i++)
			{
				name[i] = name[i + 1];
			}
			size--;
			res++;
		}
		else
		{
			isClear = 1;
		}
	}

	printf("%d", res);

	return 0;
}

