#include <stdio.h>

int findMax(int a[], int N){
    int k = 0;
    int max = a[k];
    for (int i = 0; i < N; ++i){
        if (a[i] > max){
            max = a[i];
            k = i;
        }
    }
    return k;
}

int countFreq(char* txt, char* pat, int N, int M){
    int res = 0;

    /* A loop to slide pat[] one by one */
    for (int i = 0; i <= N - M; i++)
    { 
        /* For current index i, check for 
           pattern match */
        int j;
        for (j = 0; j < M; j++)
            if (txt[i+j] != pat[j])
                break;

        // if pat[0...M-1] = txt[i, i+1, ...i+M-1]
        if (j == M)  
        {
           res++;
           j = 0;
        }
    }
    return res;
}

