    hi=n+1;
    
    while(lo<=hi){
        
        mid=lo + (hi-lo)/2;
        
        if(mid==1)
        sum_mid=n-1;
        
        else
        sum_mid= (mid-1)*(m-1) + (n-1);
        
        if(sum_mid==k){
            col_no = 0;
            break;
        }
        
        if(mid==1){
            if(k<=n-1)
            break;
        }
        
        sum_mid_1 = sum_mid - m +1;
        
        if(mid==n+1){
            col_no = k - sum_mid_1;
        }
        
        
        sum_mid_2 = sum_mid + m-1;
        
        if(k>sum_mid_1 && k<sum_mid){
            col_no = k - sum_mid_1;
                      //  printf("bgbv");

            break;
        }
        
        if(k>sum_mid && k<sum_mid_2){
            ++mid;
            col_no = k - sum_mid ;
            break;
        }
        
        if(sum_mid<k)lo=mid+1;
        
        else
        hi=mid-1;
        
        
    }
    //printf("mid : %lld ",mid);
    if(mid==1){
        col_no=k+1;
        printf("%lld 1\n",col_no);
    }
    
