    scanf("%llu %llu %llu",&n,&m,&k);

    if(k<=(n-1)+(m-1))
    {
        if(k<=n-1) printf("%llu 1",1+k);
        else
        {
            c=k-(n-1);
            printf("%llu %llu",n,1+c);
        }
    }
    else
    {
        c=(n-1)+(m-1);
        k-=c;
        x=n;
        y=m;
        up=k/(m-1);
        r=k%(m-1);
        x-=up;
        if(r==0)
        {
            if(up%2==1) y=2;
            if(up%2==0) y=m;
        }
        else if(r>0)
        {
            up++;
            r--;
            x--;
            if(up%2==1)  //go left
            {
                y=m;
                for(i=1; i<=r; i++) y--;
            }
            if(up%2==0)  //go right
            {
                y=2;
                for(i=1; i<=r; i++) y++;
            }
        }
        printf("%llu %llu",x,y);
    }

    return 0;
}

