int main()
{
    int n,m,r,c;
    long long k;
    scanf("%d%d%lld",&n,&m,&k);
    if(k<=n-1) {r=1+k;c=1;}
    else if(k<=m+n-2)
    {
        k-=(n-1);
        r=n;c=1+k;
    }
    else
    {
        k-=(m+n-2);
        r=n;c=m;
        long long quo=(k/(m-1));
        int rem=k%(m-1);
        if(quo%2) c=2;
        r-=quo;
        if(rem)
        {
            r--;
            rem--;
            if(quo%2) c+=rem;
            else c-=rem;
        }
    }
    printf("%d %d\n",r,c);
    return 0;
}

