#include <stdio.h>
#include <stdlib.h>

void swap(char*,int,int);
void del(char*,int,int*);

int main()
{
    int n;
    scanf("%d",&n);
    char*s=(char*)malloc(n*sizeof(char));
    /*for(int i=0;i<n;i++){
        scanf("%c",&s[i]);
    }*/
    scanf("%s",s);
    int action=0;
    do{
        action=0;
        for(int i=n-2;i>=0;i--){
            if(s[i]=='0'&&s[i+1]=='1'){
                swap(s,i,i+1);
                action=1;
            }
            else{
                if(s[i]=='1'&&s[i+1]=='1'){
                    del(s,i+1,&n);
                    action=1;
                }
            }
        }
    }while(action);
    s[n]='\0';
    printf("%s",s);
    return 0;
}

void swap(char* t,int i,int j){
    char help=t[i];
    t[i]=t[j];
    t[j]=help;
}

void del(char* t,int i,int* n){
    for(int j=i;j<(*n)-1;j++){
        t[j]=t[j+1];
    }
    (*n)--;
}

