#include <stdio.h>
#include <stdlib.h>
#include <stdbool.h>
#include <string.h>

bool allConditions(char *str);
void eliminateTwoOnes(char *str);
void eliminateLeadingZeros(char *str);

int main(void){
  int n;
  scanf("%d", &n);
  char *str = (char *)malloc(sizeof(char) * (n + 1));
  scanf("%s", str);
  int length = strlen(str);
  while(allConditions(str)){
      for(int i = length; i > 1; i--){
        eliminateTwoOnes(str);
        eliminateLeadingZeros(str);
        if(allConditions(str)){
          if(*(str+i) == '1' && *(str+i-1) == '0'){
            *(str+i) = '0';
            *(str+i-1) = '1';
          }
          else if(*(str+i) == '0' && *(str+i-1) == '1'){
            *(str+i) = '1';
            *(str+i-1) = '0';
          }
        }
    }
  }
  printf("%s\n", str);
}

bool allConditions(char *str){
  int length = strlen(str);
  int count = 0;
  for(int i = 0; i < length; i++){
    if(*(str+i) == '1'){
      count++;
    }
  }
  return count >= 2;
}

void eliminateTwoOnes(char *str){
  int length = strlen(str);
  for(int i = 0; i < length - 1; i++){
    if(*(str+i) == '1' && *(str+i+1) == '1'){
      for(int m = i; m < length - 1; m++){
        *(str + m) = *(str + m + 1);
      }
      *(str + length - 1) = '\0';
    }
  }
}

void eliminateLeadingZeros(char *str){
  int length = strlen(str);
  for(int i = 0; i < length; i++){
    if(*(str+i) == '1'){
      break;
    }
    else if(*(str+i) == '0'){
      for(int m = i; m < length - 1; m++){
        *(str + m) = *(str + m + 1);
      }
      *(str + length - 1) = '\0';
    }
  }
}

