#define _MAX_ 1000000005
#define _HOLES_ 14

typedef long long unsigned int llu;

int main() {
    llu holes[_HOLES_], trial[_HOLES_];

    llu i;
    for (i = 0; i < _HOLES_; i++) {
        scanf("%I64u", &holes[i]);
        trial[i] = holes[i];
    }

    llu j, k, max = 0;
    for (i = 0; i < _HOLES_; i++) {
        if (holes[i] == 0) {
            continue;
        }

        llu val = holes[i];

        llu div = val/_HOLES_;
        llu mod = val%_HOLES_;

        trial[i] = 0;

        if (div > 0) {
            for (j = 0; j < _HOLES_; j++) {
                trial[j] += div;
            }
        }

        k = i+1;
        while(mod--) {
            if (k == _HOLES_) {
                k = 0;
            }
            trial[k++]++;
        }

        llu sum = 0;
        for (j = 0; j < _HOLES_; j++) {
            if (trial[j]%2 == 0) {
                sum += trial[j];
            }
        }

        if (sum > max) {
            max = sum;
        }

        for (j = 0; j < _HOLES_; j++) {
            trial[j] = holes[j];
        }
    }

    printf("%I64u\n", max);

    return 0;
}

