        for (j = 0; j < 14; j++) {
            if (v[j] % 2 == 0) {
                ctotal += v[j];
            }
        }

        if (ctotal > total) {
            total = ctotal;
        }

        memcpy(v, vaux, sizeof vaux);
    }

    printf("%lld\n", total);
    return 0;
}

