int count(int k, int n, int s, int p)
{
    int tempResult;
    if(n%s == 0){
        tempResult = n / s;
    }
    else{
        tempResult = (n / s) + 1;
    }
    tempResult = k * tempResult;
    if(tempResult%p==0){
        return tempResult/p;
    }
    else{
        return (tempResult/p)+1;
    }
}

