int seat[200000];
int totol_seat, programmer, athlete, seated;
int pro, ath = 0;
int ji=0;

int min(int a, int b){
	return (a<b) ? a : b;
}

int main(){

	scanf("%d%d%d",&totol_seat, &programmer, &athlete);
	getchar();

	for(int i=0;i<totol_seat;i++){
		if(getchar()=='.') seat[i]=0;
			else seat[i]=1;
	}

	int empty[200000] = {0};
	int j=0;

	for (int i=0;i<totol_seat;i++) {
		if (seat[i] == 0) empty[j]++;
		else empty[++j] = 0;
	}

	for (int i=0;i<=j;i++) {
		if (empty[i]>0 && empty[i]%2==0) {
			pro += empty[i]/2;
			ath += empty[i]/2;
		} else if (empty[i] > 0){
			pro += (empty[i]-1)/2;
			ath += (empty[i]-1)/2;
			ji++;
		}
	}

	pro = min(pro,programmer);
	ath = min(ath,athlete);
	seated = pro + ath;
	if(ji>programmer-pro+athlete-ath) seated = programmer + athlete;
	else seated += ji;

	printf("%d\n", seated);
	return 0;
}

