int main(){

    int n,a,b;
    scanf("%d %d %d\n",&n,&a,&b);

    int total = a+b;
    int curr=0;

    int arr[n];

    int i;
    char c;
    for(i=0; i<n; i++){
        scanf("%c",&c);
        if(c=='\n') break;

        if(c=='*') arr[i]=1;
        else if(c=='.') arr[i]=0;
    }

    i=0;
    while(1){

        if(i==n) break;

        if(arr[i]==1) {i++;continue;}

        int no=0;
        while(1){
            if(arr[i]==1) {i++;break;}
            if(arr[i]==0 && i==n-1) {i++,no++;break;}
            i++,no++;
        }

        if(a>=b){
            a-=(no+1)/2;
            b-=((no/2));
            if(a<0)a=0;
            if(b<0)b=0;
        }
        else{
            b-=(no+1)/2;
            a-=(no/2);
            if(a<0)a=0;
            if(b<0)b=0;
        }

    }


    printf("%d\n",total-(a+b));

    return 0;

}

