char str[MAX_SIZE];
int n, a, b;
int ans=0;

void f(int x)
{
	if (a>=b){
		str[x] = 'a';
		a--;
	}
	else{
		str[x] = 'b';
		b--;
	}
}

int main()
{
	scanf("%d%d%d",&n,&a,&b);
	getchar();
	int i;
	for (i=0;i<n;i++){
		str[i] = getchar();
	}
	for (i=0;i<n;i++){
		if (a==0 && b==0)
			break;
		if (str[i]=='*')
			continue;
		if ((i==0&&str[i]=='.') || (i!=0 && (str[i-1]=='.'||str[i-1]=='*'))){
			f(i);
			ans++;
			continue;
		}
		if (str[i-1]=='a'){
			if (b==0){
				continue;
			}
			str[i] = 'b';
			b--;
			ans++;
		}
		if (str[i-1]=='b'){
			if (a==0){
				continue;
			}
			str[i] = 'a';
			a--;
			ans++;
		}
	}
	
	printf("%d",ans);
	return 0;
}

