#include<stdlib.h>

int max(int a, int b){
    return (a>b)?a:b;
}


int maxSum(int arr[], int n, int k)
{
    int i;
    if (n < k)
    {
       return -1;
    }

    int res = 0;
    for (i=0; i<k; i++)
       res += arr[i];

    int curr_sum = res;
    for (i=k; i<n; i++)
    {
       curr_sum += arr[i] - arr[i-k];
       res = max(res, curr_sum);
    }

    return res;
}

