              if(S[i]=='b'){
                FlagB=1;
                if(FlagC==1){
                    FlagNO=1;
                    break;
                }
                else{
                    BCount++;
                }
            }
              if(S[i]=='c'){
                    FlagC=1;
                    CCount++;
            }
        }
        if(FlagNO==1){
            printf("NO");
        }
        else{
            if( (CCount==BCount || CCount==ACount) && FlagB==1 && FlagC==1){
                printf("YES");
            }
            else{
                printf("NO");
            }
        }
    }

    return 0;
}

