#include <stdio.h>
#include <stdlib.h>

int solve(char *input, char pre, size_t idx) {
    int ret = 0;
    if (input[idx] == '\0') return 1;
    switch (input[idx]) {
        case 'C':
        if (pre == 'C') return 0;
        ret += solve(input, 'C', idx+1);
        break;
        case 'M' :
        if (pre == 'M') return 0;
        ret += solve(input, 'M', idx+1);
        break;
        case 'Y' :
        if (pre == 'Y') return 0;
        ret += solve(input, 'Y', idx+1);
        break;
        case '?' :
        if (pre != 'C') ret += solve(input, 'C', idx+1);
        if (pre != 'M') ret += solve(input, 'M', idx+1);
        if (pre != 'Y') ret += solve(input, 'Y', idx+1);
        break;
        default :
        ret += solve(input, input[idx], idx+1);
    }
    if (ret > 1) {
        printf("YES\n");
        exit(EXIT_SUCCESS);
    }
    return ret;
}

