int main(){
	int len, i, cnt, mulmark;
	char *s, former;

	scanf("%d", &len);
	s = (char*)malloc(len+1);
	scanf(" %s", s);

	mulmark = 0;
	if(s[0] == '?'){
		for(i = 0; s[i] == '?'; i++);
		former = s[i];
		i++;
		mulmark = 1;
	}

	for(; i < len; i++){
		switch(s[i]){
			case '?':
				for(cnt = 0; s[i] == '?' && i < len; i++, cnt++);
				if(i == len){
					mulmark = 1;
					break;
				}
				if(cnt > 1 || (cnt == 1 && s[i] == former))
					mulmark = 1;
				former = s[i];
				break;
			default:
				if(s[i] == former){
					printf("No");
					return 0;
				}
				else
					former = s[i];
		}
	}

	if(mulmark == 1)
		printf("Yes");
	else
		printf("No");
	free(s);
	return 0;
}

