int isPossible(char *str, int n){
  for(int i = 0; i < n - 1; i++){
    if(*(str+i) == *(str+i+1) && *(str+i) != '?'){
      return 1;
    }
  }
  return 0;
}

void checkPossib(char *str, int n){
  int count = 0;
  for(int i = 0; i < n; i++){
    if(*(str+i) != unpainted){
      continue;
    }
    if(i == 0){
      if(*(str+1) != unpainted){
        count += 2;
      }
      else{
        count++;
      }
    }
    else if(i > 0 && i < (n - 1)){
      if(*(str+i+1) != unpainted && *(str+i-1) != unpainted){
        if(*(str+i+1) == *(str+i-1)){
          count += 2;
        }
        else{
          count++;
        }
      }
      else{
        count += 2;
      }
    }
    else if(i == (n - 1)){
      if(*(str-1) != unpainted){
        count += 2;
      }
      else{
        count++;
      }
    }
  }
  if(count > numberOfUnpainted(str, n)){
    printf("Yes");
  }
  else{
    printf("No");
  }
}

int numberOfUnpainted(char *str, int n){
  int count = 0;
  for(int i = 0; i < n; i++){
    if(*(str+i) == unpainted){
      count++;
    }
  }
