	minute = 60 - mm;
	if(minute==60){
		minute = 0;
	}
	int h;
	if(minute==0){
		h = 20-hh;
	}
	else{
		h = 19-hh;
	}
	minute = minute + h*60;

	if(minute<0){
		minute = 0;
	}

	return minute;
}

