    {
        number_of_food = (int)number_of_food + 1;
    } 
    else
    {
        number_of_food = (int)number_of_food;
    }

    if (minutes >= 1200)
    {
        cost = cost * 0.80;
    }

    min_cost = number_of_food * cost;

    for (i = minutes; i < min_dia; i++)
    {

        if (i == 1200)
        {
            cost = cost * 0.80;
        }

        number_of_food = (float)hunger/(float)decrease;

