double checknow(int, int, int);
double check20(int, int, int, int, int, int);
double min(double, double);

int main()
{
	int hh,mm,h,d,c,n;
	scanf("%d%d%d%d%d%d",&hh,&mm,&h,&d,&c,&n);
	printf("%f\n",min(checknow(h,c,n),check20(hh,mm,h,d,c,n)));
	return 0;
}

double checknow(int h, int c, int n)
{
	int sum=0;
	do {
		h-=n;
		sum+=c;
	}
	while (h>0);
	return (double)sum;
}

double check20(int hh, int mm, int h, int d, int c, int n)
{
	double sum=0;
	if (hh>19&&hh<24) {
		do {
			h-=n;
			sum+=0.8*c;
		}
		while (h>0);
		}
	else {
		if (mm) {
			do {
				mm++;
				h+=d;
			}
			while (mm!=60);
			mm=0;
			hh++;
		}
		if (hh==20) {
			do {
				h-=n;
				sum+=0.8*c;
			}
			while (h>0);
		}
		else {
			do {
				hh++;
				h+=60*d;
			}
			while (hh!=20);
			do {
				h-=n;
				sum+=0.8*c;
			}
			while (h>0);
		}
	}
	return sum;
}

double min(double a, double b)
{
	if (a<b)
		return a;
	return b;
}

