#include <stdio.h>

double calculateDiscountMoney(int remainingDiscountTime, int hungerLevel, int hungerRate, double moneyC, int feedingRate);
double calculateNowMoney(int hungerLevel, double moneyC, int feedingRate);

int main(void){
  int hh, mm, hungerLevel, hungerRate, feedingRate;
  double moneyC;
  scanf("%d", &hh);
  scanf("%d", &mm);
  scanf("%d", &hungerLevel);
  scanf("%d", &hungerRate);
  scanf("%lf", &moneyC);
  scanf("%d", &feedingRate);

  int remainingDiscountTime = 0;
  if(hh >= 20){
    remainingDiscountTime = 0;
  }
  else if(mm != 00){
    remainingDiscountTime = 60 - mm;
    remainingDiscountTime += 60 * (20 - (hh + 1));
  }
  else{
    remainingDiscountTime =  60 * (20 - hh);
  }
  double discountMoney = calculateDiscountMoney(remainingDiscountTime, hungerLevel, hungerRate, moneyC, feedingRate);
  double buyNowMoney = calculateNowMoney(hungerLevel, moneyC, feedingRate);
  if(discountMoney <= buyNowMoney){
    printf("%.4f\n", discountMoney);
  }
  else{
    printf("%.4f\n", buyNowMoney);
  }
}

double calculateDiscountMoney(int remainingDiscountTime, int hungerLevel, int hungerRate, double moneyC, int feedingRate){
  hungerLevel = ((remainingDiscountTime * hungerRate) + hungerLevel);
  moneyC = (moneyC * 0.8);
  hungerLevel = (hungerLevel + feedingRate - 1) / feedingRate;
  return (double) hungerLevel * moneyC;
}

double calculateNowMoney(int hungerLevel, double moneyC, int feedingRate){
  hungerLevel = (hungerLevel + feedingRate - 1) / feedingRate;
  return (double) hungerLevel * moneyC;
}

