#include <stdio.h>
#include<string.h>
void check_replace(char *s)
 {
     while(*s!='\0')
     {
         if(*s=='U')
         {
             if(*(s+1)=='R')
             {
                *s='D';
                char *p=s+1;
                while(*p!='\0') {*p=*(p+1);p++;}
             }
             else
                s++;
         }
         else if (*s=='R')
         {
             if(*(s+1)=='U')
             {
                 *s='D';
                 char *p=s+1;
                 while(*p!='\0') {*p=*(p+1);p++;}
             }
             else
                s++;
         }
         else
            s++;
     }
 }
int main(void) {

	int n;
	scanf("%d",&n);
	char s[n];
	scanf("%s",s);

	check_replace(s);
	printf("%d",strlen(s));

	return 0;
}

