void countCon(char *arr, int n){
  int i = 0;
  int count = 0;
  while(i < n - 1){
    if((int)*(arr+i) != (int)*(arr+i+1)){
      count++;
      i += 2;
    }
    else {
      i++;
    }
  }
  printf("%d\n", (n - count));
}

