        arr[i]=b;
        arr2[i]=b;
    }
    for(int i  = 0; i<a-1;i++){
        if(abs(arr[i]-arr[i+1])>=2){
            if(arr[i]>arr[i+1]){
                arr[i]--;
                arr[i+1]++;
            }else{
               arr[i]++;
               arr[i+1]--; 
            }
        }
    }
    insertionSort(arr2,a);
    insertionSort(arr,a);
    for(int i = 0; i<a;i++){
        if(arr2[i]!=arr[i]){
            printf("NO");
            return 0;
        }
    }
    printf("YES");
}

