int isnext(int n){
      int m = 0 ,r = -1 ,i;
      for( i = 0 ; i < n - 1 ; i++){    if(abs(a[i] - a[i + 1]) >= 2) return -1;
      if(a[i] > m){
            r = i;
            m = a[i];
            }
      }
      if(a[i] > m)      r = i;
      return r;
}
