int main() {
	static int aa[10];
	int n, i, j, amax, imax;

	scanf("%d", &n);
	for (i = 0; i < n; i++)
		scanf("%d", &aa[i]);
	for (i = 0; i < n; i++) {
		for (j = 1; j < n - i; j++)
			if (aa[j] - aa[j - 1] >= 2 || aa[j] - aa[j - 1] <= -2) {
				printf("NO\n");
				return 0;
			}
		amax = 0;
		for (j = 0; j < n - i; j++)
			if (amax < aa[j])
				amax = aa[j], imax = j;
		for (j = imax; j < n - i - 1; j++)
			aa[j] = aa[j + 1];
		aa[n - i - 1] = amax;
	}
	printf("YES\n");
	return 0;
}

