#include <stdio.h>
#include <stdlib.h>

int main()
{
    int l, r, a, s;
    scanf("%d%d%d", &l, &r, &a);
    if(l==r)
    {
        a=a/2;
        s=l*2+a*2;
    }
    else if(l > r)
    {
        if(a>=l-r)
        {
            a=a-l+r;
            a=a/2;
            s=2*l+2*a;
        }
        else
        {
            r+=a;
            s=2*r;
        }
    }
    else if(l < r)
    {
        if(a>=r-l)
        {
            a=a-r+l;
            a=a/2;
            s=2*r+a*2;
        }
        else
        {
            l+=a;
            s=2*l;
        }
    }
    printf("%d\n", s);
    return 0;
}

