void swap(int *r, int *l) {
    int t=*r;
    *r=*l;
    *l=t;
}

int main(int argc, char *argv[])
{
    int l,r,a;
    scanf("%d%d%d",&l,&r,&a);

    if(l<r)
        swap(&l,&r);
    int d=(l-r);
    int dm = min(a,d); 
    int dx = a-dm;
    int sum=min(l,r+dm)*2+(dx/2)*2;
    //fprintf(stderr, "l:%d r:%d d:%d dm:%d dx:%d sum:%d\n",l,r,d,dm,dx,sum);
    printf("%d",sum);
    return 0;
}

