	scanf(" %d%d%d", &l,&r,&a);
	int res = min(l,r) + a;
	if (res <= l+r-min(l,r)){ printf("%d\n", res*2); }
	else{
		res = l+r-min(l,r) + (res -l-r+min(l,r))/2;
		printf("%d\n",res*2);
	}
	return 0;
}

