int main(int argc, char *argv[]) {
	int n, m, i, j;
	scanf("%d %d", &n, &m);

	int *x = (int*)malloc(sizeof(int)*n);
	int *y = (int*)malloc(sizeof(int)*m);

	for(i=0; i<n; ++i)
		scanf("%d", &x[i]);

	for(i=0; i<m; ++i)
		scanf("%d", &y[i]);

	i=0; j=0;
	int res = 0;
	while(i!=n && j!=m) {
		if(x[i] < y[j]) {
			x[i+1] += x[i];
			i++;
		} else if(x[i] > y[j]) {
			y[j+1] += y[j];
			j++;
		}

		if(x[i] == y[j]) {
			i++; j++;
			res++;
		}
	}
	printf("\n%d", res);
	return 0;
}

#include <stdio.h>

int main() {
	int n, m;
	int a[100000 + 5], b[100000 + 5];
	scanf("%d%d", &n, &m);
	for (int i = 0; i < n; i++) scanf("%d", &a[i]);
	for (int i = 0; i < m; i++) scanf("%d", &b[i]);
	int fa = 0, fb = 0;
	int ans = 0;
	while (fa < n || fb < m) {
		int suma = a[fa], sumb = b[fb];
		if (suma == sumb) {
			ans++, fa++, fb++;
			continue;
		}
		while (suma != sumb) {
			while (suma < sumb) {
				fa++, suma += a[fa];
			}
			while (suma > sumb) {
				fb++, sumb += b[fb];
			}
		}
		fa++, fb++;
		ans++;
	}
	printf("%d\n", ans);
}

