#include<stdio.h>

int main(){

    int c,d,i,j,l,n=0,s,s2;
    scanf("%d%d",&c,&d);

    int a[c],b[d];

    for(i=0;i<c;i++){
        scanf("%d",&a[i]);
    }
    for(i=0;i<d;i++){
        scanf("%d",&b[i]);
    }


    for(i=0,l=0;i<c;){
        s=a[i];
        s2=b[l];
        ekhane:
        if(s==s2){
            n++;
            i++;
            l++;
        }

        else if(s<s2){
            for(j=1;j<c;j++){

                s+=a[i+j];
                if(s==s2){
                    i=i+j+1;
                    n++;
                    l++;
                    break;
                }
                else if(s>s2){
                    i=i+j;
                    goto ekhane;
                }
            }
        }
        else{

                for(j=1;j<d;j++){
                s2+=b[l+j];
                if(s2==s){
                    l=l+j+1;
                    n++;
                    i++;
                    break;
                }
                else if(s2>s){
                    l=l+j;
                    goto ekhane;
                }
            }

        }
    }
    printf("%d",n);

    return 0;
}

