#include<stdio.h>
#include<string.h>
#define MAX 501

int r, c ;

int Judge(char pasture[][MAX], int i, int j) 
{
	int dx[4] = {-1, 0, 1, 0} ;
	int dy[4] = {0, 1, 0, -1} ;
	int symbol = 0 ;
	int k, x, y ;
	
	for(k = 0; k < 4; k++) {
		x = i + dx[k] ;
		y = j + dy[k] ;
		if(x < 0 || x >= r || y < 0 || y >= c) continue ;
		if(pasture[x][y] == 'W') {
			symbol = 1 ;
			break ;
		}
	}
	
	return symbol ;
}

int main(void)
{
	char pasture[MAX][MAX] ;
	int i, j, flag ;
		
	scanf("%d %d", &r, &c) ;
	for(i = 0; i < r; i++) {
		for(j = 0; j < c; j++) {
			scanf(" %c", &pasture[i][j]) ;
			if(pasture[i][j] == '.') {
				pasture[i][j] = 'D';
			}
		}
	}
	flag = 1 ;
	for(i = 0; i < r; i++) {
		for(j = 0; j < c; j++){
			if(pasture[i][j] == 'S') {
				if( Judge(pasture, i, j) ) {		//不可能
					printf("No\n") ; 
					flag = 0 ;
					break ;
				}
			}
		}
		if(flag == 0) {
			break ;
		} 
	}
	if(flag) {
		printf("Yes\n") ;
		for(i = 0; i < r; i++) {
			for(j = 0; j < c; j++) {
				printf("%c", pasture[i][j]) ;
			}
			printf("\n") ;
		}
	}
	
	return 0 ;
}

