char pasture[503][503];
int r, c;

int chk_ewsn(int i, int j, char sym) {
  return pasture[i+1][j] == sym || pasture[i-1][j] == sym || pasture[i][j+1] == sym || pasture[i][j-1] == sym;
}

int chk_WS(char sym) {
  for(int i = 1; i <= r; i++) {
    for(int j = 1; j <= c; j++) {
      if(pasture[i][j] == 'S') {
        if(chk_ewsn(i, j, sym)) return 1;
      }
    }
  }

  return 0;
}

void put_dog(int i, int j) {
  if(pasture[i+1][j] == '.')
    pasture[i+1][j] = 'D';

  if(pasture[i-1][j] == '.')
    pasture[i-1][j] = 'D';

  if(pasture[i][j+1] == '.')
    pasture[i][j+1] = 'D';

  if(pasture[i][j-1] == '.')
    pasture[i][j-1] = 'D';
}

void put_dogs() {
  for(int i = 1; i <= r; i++) {
    for(int j = 1; j <= c; j++) {
      if(pasture[i][j] == 'S') {
        put_dog(i, j);
      }
    }
  }
}

void dfs(int i, int j) {
  if(i <= 0 || j <= 0 || i > r || j > c)
    return;
  if(pasture[i][j] == 'S' || pasture[i][j] == 'M' || pasture[i][j] == 'D' ||pasture[i][j] == 'W')
    return;
  /*늑대 이동 : M*/
  if(pasture[i][j] == '.')
    pasture[i][j] = 'M';

  dfs(i+1, j);
  dfs(i-1, j);
  dfs(i, j+1);
  dfs(i, j-1);
}

int check() {

  /*상하좌우늑대있냐*/
  if(chk_WS('W'))
    return 0;

  put_dogs();

  for(int i = 1; i <= r; i++) {
    for(int j = 1; j <= c; j++) {
      if(pasture[i][j] == 'W')
        dfs(i, j);
    }
  }

  /*양 상하좌우에 늑대 검사*/
  if(chk_WS('M'))
    return 0;

  return 1;
}


int main() {
  memset(pasture, 0, sizeof(pasture));
  scanf("%d %d", &r, &c);

  for(int i = 1; i <= r; i++) {
