bool check(char sheep[][510],int r,int c)
{
    int i,j;
    for(i=0;i<r;i++)
    {
        for(j=0;j<c;j++)
        {
            if(sheep[i][j]=='W')
            {
                if(i-1>=0)
                {
                    if(sheep[i-1][j]=='S')return false;
                }
                if(j-1>=0)
                {
                    if(sheep[i][j-1]=='S')return false;
                }
                if(i+1<r)
                {
                    if(sheep[i+1][j]=='S')return false;
                }
                if(j+1<c)
                {
                    if(sheep[i][j+1]=='S')return false;
                }
            }
        }
    }
    return true;
}
