#include<stdio.h>
#include<inttypes.h>
int main()
{
    int64_t x,y,z;
    scanf("%I64d%I64d",&x,&y);
    while(x!=0&&y!=0)
    {
        if(x>=(2*y))
        {
           z=x/y;
           if(z%2)
           z--;
           x=x-(z*y);
        }
        
        else if(y>=(2*x))
        {
             z=y/x;
           if(z%2)
           z--;
           y=y-(z*x);
        }
        else
        break;
    }
    printf("%I64d %I64d",x,y);
    return 0;
}

#include <stdio.h>

void func(long long *a, long long *b) {
    if (*a == 0LL || *b == 0LL)
        return;
    if (*a >= *b * 2LL) {
        *a = *a % (*b * 2LL);
        func(a, b);
    }
    else if (*b >= *a * 2LL) {
        *b = *b % (*a * 2LL);
        func(a, b);
    }
}

int main(int argc, char* argv[]) {
    long long n, m;

    scanf("%I64d%I64d", &n, &m);
    func(&n, &m);
    printf("%I64d %I64d\n", n, m);

    return 0;
}

