#define REP(i, n) for((i) = 0; (i) < (n); (i)++)
#define REP1(i, n) for((i) = 1; (i) <= (n); (i)++)

int main(void)
{
    ll a, b;

    scanf("%I64d %I64d", &a, &b);

    while (a != 0 && b != 0) {
        if (a >= 2 * b) {
            a %= 2 * b;
        } else if (b >= 2 * a) {
            b %= 2 * a;
        } else {
            break;
        }
    }

    printf("%I64d %I64d\n", a, b);

    return 0;
}

