int solve(char *string, int n){
	int i, actual=0;

	for(i=0;i<n;i++){
		if(string[i] <= 'a'+actual){
			string[i] = 'a'+actual;
			actual++;
			if(actual == 26)
				break;
		}
	}
	if(actual == 26)
		return 1;
	return -1;	
}

