void solve(char arr[], int n,int max,int alpha[],int minimum){
    int temp,wanted,j,i;


    for(i=n-1;i>=0;i--){
        if(arr[i]!=max){
            wanted = arr[i];
            break;
            }
    }
    for(i=0;i<26;i++)
        if(alpha[i]>wanted && alpha[i]<=max){
            wanted=alpha[i];
            break;
        }

    for(i=n-1;i>=0;i--){
        if(arr[i]==max){
            arr[i]=minimum;
        }
        else{
            arr[i]= wanted;
            break;
        }
    }

        for(i=0;i<n;i++)
            printf("%c",arr[i]);
}
