#include <stdbool.h>
#include <stdio.h>

#define MAX_N 100000
static char _str[MAX_N+1] = {};

static bool _has_letter[26] = {};
static int minl = 100, maxl = -1;

int main() {
    int N,K; scanf("%d %d\n%s", &N, &K, _str);
    for(int i = 0 ; i < N ; ++i) {
        const int l = _str[i] - 'a';
        _has_letter[l] = true; if(l < minl) minl = l; if(l > maxl) maxl = l;
    }

    if(K > N) {
        printf("%.*s", N, _str); for(int i = N ; i < K ; ++i) { printf("%c", 'a'+minl); } printf("\n");
    } else {
        int idx = K-1;
        // skip suffix consisting of largest letter (as answer is guaranteed to exist, there is at least one different letter)
        while(idx > 0 && _str[idx] == 'a'+maxl)
            --idx;
        // increase letter
        for(int i = _str[idx] - 'a' + 1 ; i < 26 ; ++i) {
            if(_has_letter[i]) { _str[idx] = 'a' + i; break; }
        }
        // subst all letters in suffix to be minimal string
        for(int i = idx + 1 ; i < K ; ++i)
            _str[i] = 'a'+minl;
        printf("%.*s\n", K, _str);
    }

    return 0;
}

