char que[10000000];
char ans[10000000];
int main()
{
  int n , k ,i , j, flag=0;
  int indexer;
  int letters[26];
  int greatest=25;
  int greatestq=0;
  int least=0;
  int leastq=0;
  for(i=0;i<26;i++)
    letters[i]=0;
  scanf("%d %d" , &n , &k);
  scanf("%s",que );
  for(i=0;i<n;i++)
  {
    letters[que[i]%97]++;
    if(que[i]<=que[leastq])
      leastq=i;
    if(que[i]>=que[greatestq])
      greatestq=i;
  }
  least=que[leastq]%97;
  greatest=que[greatestq]%97;

  if(k>n)
  {
    for(i=0;i<k;i++)
    {
      if(i>=n)
      {
        ans[i]=que[leastq];
      }
      else
        ans[i]=que[i];
    }
  }

  if(n>k)
  {
    for(i=k-1;i>=0;i--)
    {
      if(que[i]<que[greatestq] && flag==0)
        {
          flag=1;
          j=(que[i]%97)+1;
          while(letters[j]==0)
            j++;
          ans[i]=j+97;
          indexer=i+1;
        }
      else
        ans[i]=que[i];
    }
    for(i=indexer;i<k;i++)
    {
      ans[i]=que[leastq];
    }
  }

  if(n==k)
  {
    for(i=k-1;i>=0;i--)
    {
      if(que[i]<que[greatestq] && flag==0)
        {
          flag=1;
          j=(que[i]%97)+1;
          while(letters[j]==0)
            j++;
          ans[i]=j+97;
          indexer=i+1;
        }
      else
        ans[i]=que[i];
    }
    for(i=indexer;i<k;i++)
    {
      ans[i]=que[leastq];
    }
  }
  printf("%s\n", ans);


  return 0;
}

#include <stdio.h>
#define MAX_S 100000
char stack[MAX_S], dx[255], s[MAX_S];
int alph[255];
int n, k, alph_num;

void print_stack() {
	int i;
	for (i=0; i<k; printf("%c",stack[i++] + 'a'));
	printf("\n");
}
int main () {
	int i, t, v;
	char max, ch;
	for (scanf("%d %d", &n, &k),scanf("%c",&ch), i=0; i<n; ++i) {
		scanf("%c",&ch);
		s[i]=ch-'a';
		if (!alph[(int) s[i]]) { alph_num++; alph[(int) s[i]]=1; }
	}
	for (t=0, i=0; i<255; ++i) if (alph[i]) dx[t++] = i;

	for (t=(k<n)?k:n,i=0; i<t; stack[i]=s[i],++i);
	if (n < k) {
		for (i=t; i<k; stack[i++]=dx[0]);
		print_stack();
		return 0;
	}
	max=dx[alph_num-1];
	//printf("s: ");
	//for (i=0; i<n; ++i) printf("%c", s[i]+'a');
	//printf("\n");
	//printf("alph_num: %d\n", alph_num);
	//for (i=0; i<alph_num; ++i) printf ("[%d] -> %c\n", i, dx[i]+'a');
	//printf("max: %c\n", max+'a');
	v=k-1;
	i=0;
	while (stack[v] == max) v--;
	while (dx[i++] != stack[v]);
	stack[v] = dx[i];
	for (i=v+1; i<k; ++i) stack[i]=dx[0];
	print_stack();
	return 0;
}

