#include <stdio.h>
#include <stdlib.h>
#include <math.h>
#include <string.h>

int main()
{
    int n,t,i,size;
    scanf("%d%d",&n,&t);

    if(t>n){
        size=t+1;
    }
    else{
        size=n+1;
    }

    char str[size],max='a',min='z';
    scanf("%s",str);
    int letter[26]={0};

    for(i=0;str[i]!='\0';i++){
        int x=str[i]-'a';
        if(letter[x]==0){
            letter[x]=1;
            if(str[i]>max){
                max=str[i];
            }
            if(str[i]<min){
                min=str[i];
            }
        }
    }

    if(t>n){
        for(i=n;i<t;i++){
            str[i]=min;
        }
        str[t]='\0';
        puts(str);
    }
    else{
        for(i=t-1;i>=0;i--){
            if(str[i]==max){
                str[i]=min;
            }
            else{
                int j=str[i]-'a'+1;
                for(;;j++){
                    if(letter[j]==1){
                        str[i]='a'+j;
                        break;
                    }
                }
                if(letter[j]==1){
                    break;
                }
            }
        }
        str[t]='\0';
        puts(str);
    }

    return 0;
}

#include <stdio.h>
