char s[100001];
bool f[26];
int main()
{
    int n,k,i;
    scanf("%d%d%s",&n,&k,s);
    for(i=0;i<26;i++) f[i]=false;
    char minc='z';
    char maxc='a';
    for(i=0;s[i]!='\0';i++)
    {
        if(s[i]<minc) minc=s[i];
        if(s[i]>maxc) maxc=s[i];
        f[s[i]-'a']=true;
    }
    if(k>n)
    {
        printf("%s",s);
        for(i=n;i<k;i++) printf("%c",minc);
        printf("\n");
    }
    else
    {
        int pos;
        char c;
        for(i=k-1;i>=0;i--)
        {
            if(s[i]!=maxc) {pos=i;break;}
        }
        for(i=0;i<pos;i++) printf("%c",s[i]);
        for(i=s[pos]-'a'+1;i<26;i++)
        {
            if(f[i]) {c=i+'a';break;}
        }
        printf("%c",c);
        for(i=pos+1;i<k;i++) printf("%c",minc);
        printf("\n");
    }
    return 0;
}

