	scanf("%d %d",&n,&t);

	scanf("%s",str);

	for(i=0;i<n;i++)
	{
		int temp = str[i] - 'a';

		count[temp] = 1;
	}

	for(i=0;i<26;i++)
	{
		if(count[i]==1)
		{
			break;
		}
	}

	int first = i,last;


	for(i=0;i<26;i++)
	{
		if(count[i]==1)
		{
			last =i;
		}
	}

	if(t>n)
	{
		for(i=0;i<n;i++)
		{
			ans[i] = str[i];
		}

		for(i=n;i<t;i++)
		{
			ans[i] = first + 'a';
		}
	}
	else
	{
		for(i=t-1;i>=0;i--)
		{
			if(str[i]-'a'< last)
			{
				break;
			}
			else
			{
				ans[i] = first + 'a';
			}
		}

		for(int j = str[i] - 'a' +1;j<26;j++)
		{
			if(count[j]==1)
			{
				ans[i] = j+ 'a';
				break;
			}
		}
		for(int j = i-1;j>=0;j--)
		{
			ans[j] = str[j];
		}
	}

	ans[t] = '\0';

	printf("%s\n",ans);
	return 0;
}

