	for (i=0;i<n;i++)
	{
		scanf(" %c",&Text[i]);
		Index[Text[i]-'a']=1;
	}
	
	for (i=0,j=0;i<26;i++)
	{
		if (Index[i]==1)
		    Letter[j++]=i+'a';
	}
	
	int m=j;
	
	for (i=0;i<m;i++)
	    Index[Letter[i]-'a']=i;
	
	
	if( k>n)
	{
		PrintText(n);
		for (i=n;i<k;i++)
		    printf("%c",Letter[0]);
	}
	
	else
	{
		n=k;
		
		for (i=n-1;i>=0;i--)
		{
